            Adicione seus contatos abaixo:
            <div>
                <ContactForm />
                <ContactsDetails />
            </div>
        </div>
    )
}

function ContactForm() {
    const [name, setName] = useState('');
    const [number, setNumber] = useState('');

    return (
        <div>
            <input id="name" placeholder="nome" onChange={(event) => setName(event.target.value)} />
            <input placeholder="telefone" type="phone" onChange={(event) => setNumber(event.target.value)} />
            <button type="button" onClick={() => salvarContato({ name, number })}>Salvar</button>
        </div>
    )

    function salvarContato(contact: { name: string, number: string }) {
        const updatedContacts = contacts;
        updatedContacts.push(<li key={Math.random()}> Nome: {contact.name} | Telefone: {contact.number} </li>);
        // console.log(setContacts);
        setContacts(updatedContacts);
    }
}

function ContactsDetails() {
    [contacts, setContacts] = useState([]);
    return <ol>{contacts}</ol>;
}



export default Erika;
